// Urls that the app can visit
import { Router } from "express";
import {
  createTask,
  deleteTask,
  getTask,
  getTaskCount,
  getTasks,
  updateTask,
} from "../controllers/tasks";

const router = Router();

router.get("/tasks", getTasks); // Obtener todas las tareas

router.get("/tasks/count", getTaskCount); // Obtener nm tareas

router.get("/tasks/:id", getTask); // Obtener una tarea

router.post("/tasks", createTask); // Aadir una tarea

router.delete("/tasks/:id", deleteTask); // borra una unica tarea

router.put("/tasks/:id", updateTask); // Actualizar una tarea

export default router;

