sourcesContent:
// Urls that the app can visit
import { Router } from "express";
import {
  createUser,
  deleteUser,
  getUser,
  getUsersCount,
  getUsers,
  updateUser,
} from "../controllers/users";

const router = Router();

router.get("/users", getUsers); // Obtener todas las tareas

router.get("/users/count", getUsersCount); // Obtener nm tareas

router.get("/users/:id", getUser); // Obtener una tarea

router.post("/users", createUser); // Aadir una tarea

router.delete("/users/:id", deleteUser); // borra una unica tarea

router.put("/users/:id", updateUser); // Actualizar una tarea

export default router;

