// Answer to a url from app
import { connect } from "../database";

///////////////////////////////////////////////////////////////////
// USERS FUNCTIONS
///////////////////////////////////////////////////////////////////

///////////////////////////////////////////////////////////////////
// Function to get all the users
//
export const getUsers = async (req, res) => {
  // Connection to the database
  const connection = await connect();

  // Query to the database
  const [users] = await connection.query("SELECT * FROM users");

  // Send the response
  res.json(users);
};

///////////////////////////////////////////////////////////////////
// Function to get one user by id
//
export const getUser = async (req, res) => {
  // Connection to the database
  const connection = await connect();

  // Query to the database
  const [user] = await connection.query("SELECT * FROM users WHERE id = ?", [
    req.params.id,
  ]);

  // Check if the user exists
  if (user.length === 0) {
    return res.status(404).json({ message: "User not found" });
  } else {
    // Send the response
    res.json(user[0]);
  }
};

///////////////////////////////////////////////////////////////////
// Function to get one user by username
//
export const getUserByUsername = async (req, res) => {
  try {
    console.log("Requested name:", req.body.name); // Log the input name
    const connection = await connect();
    const [users] = await connection.query(
      "SELECT * FROM users WHERE name = ?",
      [req.body.name]
    );

    console.log("Query result:", users); // Log the query result
    if (users.length === 0) {
      return res.status(404).json({ message: "User not found" });
    } else {
      res.json(users[0]);
    }
  } catch (error) {
    res.status(500).json({ message: "Server error", error: error.message });
  }
};

///////////////////////////////////////////////////////////////////
// Function to get one user by email
//
export const getUserByEmail = async (req, res) => {
  try {
    // Connection to the database
    const connection = await connect();

    // Query to the database
    const [users] = await connection.query(
      "SELECT * FROM users WHERE email = ?",
      [req.params.email]
    );

    // Check if the user exists
    if (users.length === 0) {
      return res.status(404).json({ message: "User not found" });
    } else {
      // Send the response
      res.json(users[0]);
    }
  } catch (error) {
    res.status(500).json({ message: "Server error", error: error.message });
  }
};

///////////////////////////////////////////////////////////////////
// Function to get the number of users
//
export const getUsersCount = async (req, res) => {
  // Connection to the database
  const connection = await connect();

  // Query to the database
  const [numUsers] = await connection.query("SELECT count(*) FROM users");

  // Send the response
  res.json(numUsers[0]["count(*)"]);
};

///////////////////////////////////////////////////////////////////
// Function to create a user
export const createUser = async (req, res) => {
  // Check if required fields are present
  if (
    !req.body.name ||
    !req.body.email ||
    !req.body.username ||
    !req.body.password
  ) {
    return res.status(400).json({ error: "All fields are required." });
  }

  try {
    // Connection to the database
    const connection = await connect();

    // Hash the password
    //const hashedPassword = await bcrypt.hash(req.body.password, 10);
    const hashedPassword = req.body.password;

    // Query to the database
    const [results] = await connection.query(
      "INSERT INTO users (name, email, username, password) VALUES (?, ?, ?, ?)",
      [req.body.name, req.body.email, req.body.username, hashedPassword]
    );

    // Successful response
    res.json({
      id: results.insertId,
      name: req.body.name,
      email: req.body.email,
      username: req.body.username,
    });
  } catch (error) {
    // Log error
    console.error("Fallo al creaar el usuario:", error);
    return res.status(500).json({ error: "Fallo al creaar el usuario." });
  }
};

///////////////////////////////////////////////////////////////////
// Function to delete a user
//
export const deleteUser = async (req, res) => {
  // Connection to the database
  const connection = await connect();

  // Query to the database
  await connection.query("DELETE FROM users WHERE id = ?", [req.params.id]);

  // Send status
  res.sendStatus(204);
};

///////////////////////////////////////////////////////////////////
// Function to update a user
//
export const updateUser = async (req, res) => {
  // Connection to the database
  const connection = await connect();

  // Query to the database
  const result = await connection.query("UPDATE users SET ? WHERE id = ?", [
    req.body,
    req.params.id,
  ]);

  // Send the info of the new user
  res.json({ ...req.body, id: req.params.id });
};

///////////////////////////////////////////////////////////////////
// Function to login a user
//
export const loginUser = async (req, res) => {
  // Connection to the database
  const connection = await connect();

  // Query to the database
  const [user] = await connection.query(
    "SELECT * FROM users WHERE username = ? AND password = ?",
    [req.body.username, req.body.password]
  );

  // Check if the user exists
  if (user.length === 0) {
    return res.status(404).json({ message: "User not found" });
  } else {
    // Send the response
    res.json(user[0]);
  }
};

